select users.* from wkb_user users,wkb_friends friends
where users.u_id=friends.f_uid and (friends.u_id=#{userId} or friends.f_uid=#{userId})
and users.u_id!=#{userId}
order by users.u_name limit #{startPos},#{endPos}
